import React, { Component } from 'react';
import './Square.css';
import Popup from './Popup';
import Draggable from 'react-draggable';
class Square extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Id:this.props.Id,
            showPopup:false,
            isHighlighted:false,
            products : [],
            latlng:{
                latitude:this.props.latlng.latitude,
                longitude:this.props.latlng.longitude,
                latitudeDelta:0.0922,
                longitudeDelta:0.0421
            }
        };
    }

    componentDidMount() {
        fetch('/admin', {
            method: "post",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                id:this.state.id
            })
        }).then(res => res.json())
        .then(data => {
            this.setState({
                products:data
            });
        });
    }

    togglePopup() {  
        this.setState({  
             showPopup: !this.state.showPopup  
        });  
    } 

    render() {
        return (
            //{<Draggable
            //class='draggable'
            //defaultPosition={{x: 0, y: 0}}
            //onStop={this.handleStop}
            //>}
                <div class='square-grid'>
                    <div class='square-grid__cell square-grid__cell--9'>
                        <div class='square-grid__content'>
                            <button class='button' onClick={this.togglePopup.bind(this)}> I </button>
                            {this.state.showPopup ?  <Popup products={this.state.products} 
                                                    closePopup={this.togglePopup.bind(this)}/>  : null}
                        </div>
                    </div>
                </div>
            //{</Draggable>}         
        );
    }
}

export default Square;
