import React, { Component } from 'react';
import './Square.css';
import Popup from './Popup';
class Square extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Id:this.props.Id,
            showPopup:false,
            isHighlighted:false,
            products : [],
            latlng:{
                latitude:this.props.latlng.latitude,
                longitude:this.props.latlng.longitude,
                latitudeDelta:0.0922,
                longitudeDelta:0.0421
            }
        };
    }

    componentDidMount() {
        fetch('/admin', {
            method: "post",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                id:this.state.id
            })
        }).then(res => res.json())
        .then(data => {
            this.setState({
                products:data
            });
        });
    }

    togglePopup() {  
        this.setState({  
             showPopup: !this.state.showPopup  
        });  
    } 

    render() {
        return (
            <div id="squareContainer">
                <button onClick={this.togglePopup.bind(this)}> Items </button>
                {this.state.showPopup ?  <Popup products={this.state.products} closePopup={this.togglePopup.bind(this)}/>  : null}
            </div>
        );
    }
}

export default Square;
