export function browserDownload(json) {
  let output=JSON.parse(JSON.stringify(json));
  console.log(output);
  fetch('http://192.168.43.115:5000/warehouseSave', {
            method: "post",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                data:output
            })
        })
}

